import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import './App.css'; // Import the CSS file

const SERVER_IP = 'http://192.168.0.27:4000'; // Define server IP address here
const socket = io(SERVER_IP);

function App() {
  const [msg, setMsg] = useState('');
  const [userName, setUserName] = useState('');
  const [chat, setChat] = useState([]);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [preview, setPreview] = useState(null);

  const send = (e) => {
    e.preventDefault();
    if (!userName) {
      alert('Please enter a username');
      return;
    }
    const messageData = { userName, msg, timestamp: new Date().toISOString() };
    socket.emit('msg', messageData);
    setMsg('');
  };

  const uploadFile = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('Please select a file to upload');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post(`${SERVER_IP}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (res.data) {
        const { fileName, filePath } = res.data;
        const fileData = { userName, fileName, filePath, timestamp: new Date().toISOString() };
        socket.emit('file-upload', fileData);
        setFileName('');
        setFile(null);
        setPreview(null);
        document.getElementById('fileInput').value = null; // Reset file input
      } else {
        console.error('No response data');
      }
    } catch (err) {
      console.error('Error uploading file:', err);
      if (err.response) {
        console.error('Response error:', err.response.data);
      }
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setFileName(selectedFile.name);

    if (selectedFile && selectedFile.type.startsWith('image/')) {
      const previewUrl = URL.createObjectURL(selectedFile);
      setPreview(previewUrl);
    } else {
      setPreview(null);
    }
  };

  useEffect(() => {
    // Add event listener for incoming messages
    const historyListener = (history) => {
      setChat(history);
    };

    socket.on('history', historyListener);

    // Load chat history initially
    socket.emit('load-history');

    // Clean up the event listener when the component unmounts
    return () => {
      socket.off('history', historyListener);
    };
  }, []);

  const formatTimestamp = (timestamp) => {
    try {
      if (!timestamp) return 'Invalid Date';
      const date = new Date(timestamp);
      return new Intl.DateTimeFormat('ja-JP', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        timeZone: 'Asia/Tokyo'
      }).format(date);
    } catch (error) {
      console.error('Error formatting timestamp:', error); // Debug log
      return 'Invalid Date';
    }
  };

  return (
    <div className="App">
      <h1>Chat program using Socket.io</h1>
      <form onSubmit={send} className="chat">
        <input
          type="text"
          required
          placeholder="Username here..."
          name="username"
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
        />
        <input
          type="text"
          required
          placeholder="Message here..."
          name="msg"
          value={msg}
          onChange={(e) => setMsg(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
      <form onSubmit={uploadFile} className="chat">
        <input
          type="file"
          id="fileInput"
          onChange={handleFileChange}
        />
        <button type="submit">Upload</button>
      </form>
      <div className="chatMsg">
        {chat.map((myData, index) => (
          <p key={index}>
            {myData.msg && (
              <>
                <span><strong>{myData.userName}:</strong> {myData.msg}</span>
                <span className="timestamp">{formatTimestamp(myData.timestamp)}</span>
              </>
            )}
            {myData.fileName && (
              <>
                <span>
                  <strong>{myData.userName}:</strong> 
                  {myData.filePath.match(/\.(jpeg|jpg|gif|png)$/) != null ? (
                    <img src={`${SERVER_IP}/uploads/${myData.fileName}`} alt={myData.fileName} style={{ maxWidth: '200px', maxHeight: '200px' }} />
                  ) : (
                    <a href={`${SERVER_IP}/uploads/${myData.fileName}`} target="_blank" rel="noopener noreferrer">{myData.fileName}</a>
                  )}
                </span>
                <span className="timestamp">{formatTimestamp(myData.timestamp)}</span>
              </>
            )}
          </p>
        ))}
      </div>
    </div>
  );
}

export default App;
