import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import './Chat.css';

const SERVER_IP = 'http://192.168.0.27:4000';
const socket = io(SERVER_IP);

function Chat() {
  const [msg, setMsg] = useState('');
  const [userName, setUserName] = useState('');
  const [chat, setChat] = useState([]);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      window.location.href = '/';
      return;
    }
    axios.post(`${SERVER_IP}/verifyToken`, { token }).then((res) => {
      if (res.data.success) {
        setUserName(res.data.username);
      } else {
        localStorage.removeItem('token');
        window.location.href = '/';
      }
    });

    const messageListener = (myData) => {
      setChat((prevChat) => [...prevChat, myData]);
    };

    const fileListener = (fileData) => {
      setChat((prevChat) => [...prevChat, fileData]);
    };

    socket.on('msg', messageListener);
    socket.on('file-upload', fileListener);

    return () => {
      socket.off('msg', messageListener);
      socket.off('file-upload', fileListener);
    };
  }, []);

  const send = (e) => {
    e.preventDefault();
    if (!userName) {
      alert('Please enter a username');
      return;
    }
    socket.emit('msg', { userName, msg });
    setMsg('');
  };

  const uploadFile = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('Please select a file to upload');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post(`${SERVER_IP}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (res.data) {
        const { fileName, filePath } = res.data;
        socket.emit('file-upload', { userName, fileName, filePath });
        setFileName('');
        setFile(null);
        setPreview(null);
        document.getElementById('fileInput').value = null; // Reset file input
      } else {
        console.error('No response data');
      }
    } catch (err) {
      console.error('Error uploading file:', err);
      if (err.response) {
        console.error('Response error:', err.response.data);
      }
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setFileName(selectedFile.name);

    if (selectedFile && selectedFile.type.startsWith('image/')) {
      const previewUrl = URL.createObjectURL(selectedFile);
      setPreview(previewUrl);
    } else {
      setPreview(null);
    }
  };

  return (
    <div className="App">
      <h1>Chat program using Socket.io</h1>
      <form onSubmit={send} className="chat">
        <input
          type="text"
          required
          placeholder="Message here..."
          name="msg"
          value={msg}
          onChange={(e) => setMsg(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
      <form onSubmit={uploadFile} className="chat">
        <input type="file" id="fileInput" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      <div className="chatMsg">
        {chat.map((myData, index) => (
          <p key={index}>
            {myData.msg && (
              <span>
                <strong>{myData.userName}:</strong> {myData.msg}
              </span>
            )}
            {myData.fileName && (
              <span>
                <strong>{myData.userName}:</strong>
                {myData.filePath.match(/\.(jpeg|jpg|gif|png)$/) != null ? (
                  <img src={myData.filePath} alt={myData.fileName} style={{ maxWidth: '200px', maxHeight: '200px' }} />
                ) : (
                  <a href={myData.filePath} target="_blank" rel="noopener noreferrer">
                    {myData.fileName}
                  </a>
                )}
              </span>
            )}
            {myData.timestamp && <span>{new Date(myData.timestamp).toLocaleString('ja-JP')}</span>}
          </p>
        ))}
      </div>
    </div>
  );
}

export default Chat;
